mapper.select:
SELECT distinct s.source_no, s.source_name, s.bpm, s.key, s.source_time, t.tag_no, t.genre, p.pack_no, pi.pack_img_storedname, pi.pack_img_originname, sf.file_originname, sf.file_storedname, (SELECT count(*) FROM source_like sl where s.source_no = sl.source_no and sl.user_no= #{userNo}) cnt, sif.source_img_originname, sif.source_img_storedname FROM source S left JOIN tag t on s.tag_no = t.tag_no left JOIN pack p on s.pack_no = p.pack_no left JOIN packimginfo pi on p.pack_no = pi.pack_no left JOIN sourcefileinfo sf on s.source_no = sf.source_no left JOIN sourceimginfo sif on s.source_no = sif.source_no WHERE t.genre = #{genre}
AND t.instrument = #{instrument}
AND t.scape = #{scape}
AND t.detail = #{detail}
AND t.fx = #{fx}
order by source_no

  		SELECT DISTINCT GENRE FROM TAG
  		WHERE 1=1

  			AND DETAIL = #{detail}


			AND INSTRUMENT = #{instrument}

  		SELECT DISTINCT SCAPE FROM TAG
  		WHERE 1=1

  			AND DETAIL = #{detail}


			AND INSTRUMENT = #{instrument}

  		SELECT DISTINCT FX FROM TAG
  		WHERE 1=1

  			AND DETAIL = #{detail}


			AND INSTRUMENT = #{instrument}

  		SELECT DISTINCT DETAIL FROM TAG
  		WHERE 1=1

  			AND DETAIL = #{detail}


			AND INSTRUMENT = #{instrument}

SELECT distinct s.source_no, s.source_name, s.bpm, s.key, s.source_time, t.tag_no, t.genre, p.pack_no, pi.pack_img_storedname, pi.pack_img_originname, sf.file_originname, sf.file_storedname, (SELECT count(*) FROM source_like sl where s.source_no = sl.source_no and sl.user_no= #{userNo}) cnt, sif.source_img_originname, sif.source_img_storedname FROM source S INNER JOIN tag t on s.tag_no = t.tag_no INNER JOIN pack p on s.pack_no = p.pack_no LEFT JOIN packimginfo pi on p.pack_no = pi.pack_no INNER JOIN sourcefileinfo sf on s.source_no = sf.source_no LEFT JOIN sourceimginfo sif on s.source_no = sif.source_no WHERE t.instrument = #{instrument}
AND t.genre = #{genre}
AND t.detail = #{detail}
AND t.scape = #{scape}
AND t.fx = #{fx}
ORDER BY S.SOURCE_NO

  		SELECT sif.file_originname, sif.file_storedname,
  			s.bpm, s.key, s.source_time, s.source_no,
            p.pack_no, p.pack_name, p.pack_content,
            pig.pack_img_storedname,
            t.tag_no
		FROM Pack p
		left JOIN source s on s.pack_no = p.pack_no
		left JOIN packimginfo pig on pig.pack_no = p.pack_no
		left JOIN tag t on t.tag_no = s.tag_no
		left JOIN sourcefileinfo sif on s.source_no = sif.source_no
		WHERE 1=1 and p.pack_no = #{packNo}


			AND t.genre = #{genre}



			AND t.detail = #{detail}



			AND t.instrument = #{instrument}



			AND t.scape = #{scape}



			AND t.fx = #{fx}


		order by s.source_no desc

SELECT p.pack_no, p.pack_name, t.tag_no, t.genre, t.instrument, pif.pack_img_storedname, sif.file_storedname From Pack p INNER JOIN tag t on t.tag_no = p.tag_no INNER JOIN SOURCE s on s.pack_no = p.pack_no INNER JOIN SOURCEFILEINFO sif on sif.source_no = s.source_no INNER JOIN PACKIMGINFO pif on pif.pack_no = p.pack_no WHERE 1=1 and lower(sif.file_originname) Like '%main%'
and t.genre = #{genre}
and t.instrument = #{instrument}
and t.detail = #{detail}
order by p.pack_no desc
